import styled, { css } from 'styled-components';
import { shade } from 'polished';

interface FormProps{
  hasError: boolean;
}

export const Button = styled.button`
background-color:transparent;
color: #FFE81F;
border: 0;
display: flex;
align-items: center;
text-decoration: none;
color: #FFE81F;
transition: color 0.2s;
font-size:16px;
`;

export const Header = styled.h1`
  color:  #FFE81F;
  text-decoration: none;
`;

export const Title = styled.h1`
  font-size: 48px;
  color: #FFE81F;
  max-width: 450px;
  line-height: 56px;


  margin-top: 80px
`;

export const Form = styled.form<FormProps>`
  margin-top: 40px;
  max-width: 700px;

  display: flex;

  input{
    color: #3A3A3A;
    flex: 1;
    height: 70px;
    padding: 0 24px;
    border: 0;
    border-radius: 5px 0 0 5px;
    border: 2px solid #fff;
    border-right: 0;

    ${(props) => props.hasError && css`
      border-color: #c53030;
    `}

    &::placeholder {
      color: #a8a8b3;
    }
  }

  button{
    width: 210px;
    height: 70px;
    background: #FFE81F;
    border-radius: 0px 5px 5px 0px;
    border:0;
    color: #fff;
    font-weight:bold;
    transition: background-color 0.2s;
    &:hover{
    background: ${shade(0.2, '#CCCC00')}

  }

  }

`;

export const Error = styled.span`
  display: block;
  color: #c53030;
  margin-top: 8px
`;

export const Info = styled.div`
margin-top: 80px;
max-width: 700px;

p{
  color: #FFE81F;
  font-size: 20px;

}

a{
  background: #fff;
  border-radius: 5px;
  width: 100%;
  padding: 24px;
  display: block;
  text-decoration: none;

  display: flex;
  align-items: center;
  transition: transform 0.2s;

  &+a{
    margin-top: 16px
  }



img{
  width:64px;
  height: 64px;
  border-radius: 50%

}

div{
  margin: 0 16px;
  flex: 1;

  svg{
    margin-left: ;
    color: #FFE81F;
  }

  .NormalTitle{
    margin-left: 20px;
  }

  strong{
    font-size: 20px;
    color: #3D3D4D;

  }

  p{
    font-size: 18px;
    color: #A8A8B3;
    margin-top: 4px;
    margin-left: 20px;
  }
}
svg{
    margin-right: 5px;
    color: #FFE81F;
  }

  &:hover{
    transform: translateX(10px)
  }

}
`;
