import {} from "@material-ui/core";
import api from "../../services/api";
import { Title, Form, Info, Error, Header, Button } from "./styles";

interface CharacterInfo {
  favorited: boolean;
  count: number;
  results: [
    {
      name: string;
      height: number;
      mass: number;
      hair_color: string;
      skin_color: string;
      eye_color: string;
      birth_year: string;
      gender: string;
      homeworld: string;
      films: [];
    }
  ];
}

interface HomeworldInfo {
  name: string;
}

const Dashboard: React.FunctionComponent = () => {
  const [newRepo, setNewRepo] = useState("");
  const [inputError, setInputError] = useState("");
  const [favorited, setFavorite] = useState<CharacterInfo[]>(() => {
    const storagedCharacter = localStorage.getItem("@githubExplorer:Character");
    if (storagedCharacter) {
      return JSON.parse(storagedCharacter); // desconvertendo para um array
    }
