/* eslint-disable max-len */
import React, { useState, FormEvent, useEffect } from "react";
import { FiChevronRight, FiStar } from "react-icons/fi";
import { GoStar, GrFavorite } from "react-icons/all";
import { Link } from "react-router-dom";
import {} from "@material-ui/core";
import api from "../../services/api";
import "./styles.scss";
// import { Title, Form, Info, Error, Header, Button } from "./styles";
import Logo from "../../assets/logo.png";
//@ts-ignore
import handwriting from "handwriting.js";
//@ts-ignore
import handwriting2 from "handwriting.js";
//@ts-ignore
import iink from "iink-js";

const Dashboard = () => {
  const [imagePath, setImagePath] = useState("");
  const [text, setText] = useState("");
  console.log("handwriting1", handwriting);
  console.log("handwriting2", handwriting2);
  //@ts-ignore
  // const canvasI = new handwriting.Canvas(document.getElementById("can"));
  // console.log("canvas1", canvasI);
  const editorElement = document.getElementById("editor");

  iink.register(editorElement, {
    recognitionParams: {
      type: "TEXT",
      server: {
        applicationKey: "b259a3a3-4ec7-4086-8d9d-d3215a89d85a",
        hmacKey: "63773195-5e31-4bfd-a593-a8f5a6a875bb",
      },
    },
  });

  return (
    <>
      <div
        className="header"
        style={{
          display: "flex",
          position: "sticky",
          height: "100px",
          width: "100%",
          alignItems: "center",
          backgroundColor: "#FFD5AD",
          top: 0,
        }}
      >
        <div
          style={{
            zIndex: -2,
            display: "flex",
            position: "absolute",
            height: "100px",
            width: "100%",
            alignItems: "center",
            backgroundColor: "#FFD5AD",
            top: 0,
          }}
          className="header-img"
        ></div>
        <div
          style={{
            width: "100%",
            margin: "0px 40px",
            display: "flex",
            justifyContent: "space-between",
          }}
        >
          <img
            style={{
              position: "absolute",
              marginTop: "-20px",
              marginLeft: "-20px",
              display: "block",
              width: "60px",
              height: "60px",
            }}
            src={Logo}
            alt="aa"
          />
          <div>
            <span style={{ marginLeft: "50px" }}>Contei</span>
          </div>

          <div
            className="home-buttons"
            style={{
              display: "flex",
              gap: "50px",
              textDecorationColor: "#FF8B1F",
            }}
          >
            <span>Home</span>
            <span>Sobre</span>
            <span>Fale conosco</span>
            <span></span>
          </div>
        </div>
      </div>

      <div
        style={{
          background: "gray",
          height: "100%",
          padding: "50px 50px",
          overflow: "auto",
        }}
      >
        <div id="editor"></div>
        <canvas
          id="can"
          width="400"
          height="400"
          style={{ border: "2px solid", cursor: "crosshair" }}
        ></canvas>
        <span>Contei</span>
        <span>Explore Star Wars Characters</span>
      </div>
    </>
  );
};

export default Dashboard;
